jQuery(".quote-request").on("click",function(e){e.preventDefault(),jQuery.ajax({method:"GET",url:red_vars.rest_url+"wp/v2/posts/",beforeSend:function(e){e.setRequestHeader("X-WP-Nonce",red_vars.wpapi_nonce)}}).done(function(e){for(let n=0;n<1;n++)jQuery(".article-home").append(`<div class="ajax-content"><p>${e[0].content.rendered}</p><h2> - ${e[0].title.rendered}</h2></div>`),console.log(e)})});